import { required, email, minLength, maxLength, sameAs } from "vuelidate/lib/validators";
import { nombreCompuesto } from "@/utils/validaciones";
export default {
  data: function data() {
    return {
      vista: 2,
      f1: {
        email: "",
        password: "",
        repetirPassword: ""
      },
      f2: {
        nombres: "",
        apellidos: ""
      },
      fechaNacimiento: null,
      fechaMaxima: null
    };
  },
  validations: {
    f1: {
      email: {
        required: required,
        email: email
      },
      password: {
        required: required,
        minLength: minLength(6),
        maxLength: maxLength(20)
      },
      repetirPassword: {
        sameAs: sameAs("password")
      }
    },
    f2: {
      nombres: {
        required: required,
        minLength: minLength(3),
        maxLength: maxLength(20),
        nombreCompuesto: nombreCompuesto
      },
      apellidos: {
        required: required,
        minLength: minLength(3),
        maxLength: maxLength(20),
        nombreCompuesto: nombreCompuesto
      }
    },
    fechaNacimiento: {
      required: required
    }
  },
  created: function created() {
    var fechaActual = new Date();
    this.fechaMaxima = new Date(fechaActual.setFullYear(fechaActual.getFullYear() - 13)).toISOString().substr(0, 10);
  },
  methods: {
    siguiente: function siguiente(vista) {
      switch (vista) {
        case 1:
          if (this.$v.f1.$invalid) {
            this.$v.f1.$touch();
            return;
          } else {
            this.vista++;
          }

          break;

        case 2:
          if (this.$v.f2.$invalid) {
            this.$v.f2.$touch();
            return;
          } else {
            this.vista++;
          }

          break;
      }
    },
    registrar: function registrar() {
      if (this.$v.fechaNacimiento.$invalid) {
        return;
      }

      alert("Registrando...");
    },
    enter: function enter() {
      if (this.vista == 3 && !this.fechaNacimiento) {
        this.$refs.calendario.activePicker = "YEAR";
      }
    }
  },
  computed: {
    erroresEmail: function erroresEmail() {
      var errores = [];

      if (!this.$v.f1.email.$dirty) {
        return errores;
      }

      if (!this.$v.f1.email.required) {
        errores.push("Ingresa tu email.");
      }

      if (!this.$v.f1.email.email) {
        errores.push("Ingresa un email válido.");
      }

      return errores;
    },
    erroresPassword: function erroresPassword() {
      var errores = [];

      if (!this.$v.f1.password.$dirty) {
        return errores;
      }

      if (!this.$v.f1.password.required) {
        errores.push("Ingresa tu password.");
      }

      if (!this.$v.f1.password.minLength) {
        errores.push("Ingresa al menos 6 caracteres.");
      }

      if (!this.$v.f1.password.maxLength) {
        errores.push("Ingresa máximo 20 caracteres.");
      }

      return errores;
    },
    erroresRepetirPassword: function erroresRepetirPassword() {
      var errores = [];

      if (!this.$v.f1.repetirPassword.$dirty) {
        return errores;
      }

      if (!this.$v.f1.repetirPassword.sameAs) {
        errores.push("Las contraseñas no coinciden.");
      }

      return errores;
    },
    erroresNombres: function erroresNombres() {
      var errores = [];

      if (!this.$v.f2.nombres.$dirty) {
        return errores;
      }

      if (!this.$v.f2.nombres.required) {
        errores.push("Ingresa tu nombre.");
      }

      if (!this.$v.f2.nombres.minLength) {
        errores.push("Ingresa al menos 3 caracteres.");
      }

      if (!this.$v.f2.nombres.maxLength) {
        errores.push("Ingresa máximo 20 caracteres.");
      }

      if (!this.$v.f2.nombres.nombreCompuesto) {
        errores.push("Ingresa un nombre válido.");
      }

      return errores;
    },
    erroresApellidos: function erroresApellidos() {
      var errores = [];

      if (!this.$v.f2.apellidos.$dirty) {
        return errores;
      }

      if (!this.$v.f2.apellidos.required) {
        errores.push("Ingresa tus apellidos.");
      }

      if (!this.$v.f2.apellidos.minLength) {
        errores.push("Ingresa al menos 3 caracteres.");
      }

      if (!this.$v.f2.apellidos.maxLength) {
        errores.push("Ingresa máximo 20 caracteres.");
      }

      if (!this.$v.f2.apellidos.nombreCompuesto) {
        errores.push("Ingresa un apellido válido.");
      }

      return errores;
    }
  }
};
