import {
  required,
  email,
  minLength,
  maxLength
} from "vuelidate/lib/validators";
import { setTimeout } from "timers";
import { mapGetters, mapMutations } from "vuex";


export default {
  data() {
    return {
      formulario: {
        email: "",
        password: ""
      }
    };
  },
  validations: {
    formulario: {
      email: {
        required,
        email
      },
      password: {
        required,
        minLength: minLength(6),
        maxLength: maxLength(20)
      }
    }
  },
  methods: {
    ...mapMutations(['mostrarOcupado', 'ocultarOcupado', 'mostrarExito']),
    ...mapMutations('sesion', ['actualizarUsuario']),
    ingresar() {
      if (this.$v.formulario.$invalid) {
        this.$v.formulario.$touch();
        return;
      }

      let usuario = {
        userName: "newton",
        nombres: "Isaac",
        apellidos: "Newton",
        sexo: "M",
        descripcion: "Descripción",
        biografia: "https://es.wikipedia.org/wiki/Isaac_Newton",
        fotoPerfil:
          "https://upload.wikimedia.org/wikipedia/commons/thumb/8/83/Sir_Isaac_Newton_%281643-1727%29.jpg/220px-Sir_Isaac_Newton_%281643-1727%29.jpg"
      };

      let ocupado = {
        titulo: "Validando credenciales",
        mensaje: "Estamos validando tu información..."
      };

      this.mostrarOcupado(ocupado);

      setTimeout(() => {
        this.ocultarOcupado()
        this.actualizarUsuario(usuario);
        this.mostrarExito(this.saludo);
      }, 1000);
    }
  },
  computed: {
    ...mapGetters('sesion', ['saludo']),
    erroresEmail() {
      let errores = [];
      if (!this.$v.formulario.email.$dirty) {
        return errores;
      }
      if (!this.$v.formulario.email.required) {
        errores.push("Ingresa tu email.");
      }
      if (!this.$v.formulario.email.email) {
        errores.push("Ingresa un email válido.");
      }
      return errores;
    },
    erroresPassword() {
      let errores = [];
      if (!this.$v.formulario.password.$dirty) {
        return errores;
      }
      if (!this.$v.formulario.password.required) {
        errores.push("Ingresa tu password.");
      }
      if (!this.$v.formulario.password.minLength) {
        errores.push("Ingresa al menos 6 caracteres.");
      }
      if (!this.$v.formulario.password.maxLength) {
        errores.push("Ingresa máximo 20 caracteres.");
      }
      return errores;
    }
  }
};
